    #--------main function--------------
    def BtnA1Fn(self):
        btn=self.sender()
        if self.Qrad1.isChecked():
            self.constraintControlCreate(int(btn.text()))
        if self.Qrad2.isChecked():
            self.parentControlCreate(int(btn.text())) 
        if self.Qrad3.isChecked():
            self.replaceCurveControl(int(btn.text()))
        if self.clickWithclose.checkState()==Qt.CheckState.Unchecked:
            self.close()
            
    def mousePressEvent(self,event):
        if event.button()==Qt.LeftButton or event.button()==Qt.RightButton:
            self.m_drag=True
            self.m_DragPosition=event.globalPos()-self.pos()
            event.accept()
            
    def mouseMoveEvent(self,QMouseEvent):
        if QMouseEvent.buttons() and Qt.LeftButton:
            self.move(QMouseEvent.globalPos()-self.m_DragPosition)
            QMouseEvent.accept()
            
    def mouseReleaseEvent(self,QMouseEvent):
        self.m_drag=False
        
    def closeEvent(self, event):
        self.deleteLater()

    def cleanOpenWindow(self):
        gmw = getMayaWindow()
        for g in gmw.children():
            try:
                Tit = g.windowTitle()
                if Tit == self.titleName:
                    g.close()
                    g.setParent(None)
            except:
                pass


