    def constraintControlCreate(self,shapeNum):
        sel=cmds.ls(sl=1)
        if sel:
            for s in sel:    
                crv=self.curveShapes(shapeNum,objName=s+'_crv')
                ggrp=cmds.group(crv,n=s+'_grp')
                dgrp=cmds.group(ggrp,n=s+'_sdk')
                zgrp=cmds.group(dgrp,n=s+'_zero')
                
                getTrans=cmds.xform(s,q=1,ws=1,t=1)
                getRot=cmds.xform(s,q=1,ws=1,ro=1)
                getScale=cmds.xform(s,q=1,ws=1,s=1)
                cmds.xform(zgrp,ws=1,t=getTrans)
                cmds.xform(zgrp,ws=1,ro=getRot)
                cmds.xform(zgrp,ws=1,s=getScale)
                
                cmds.parentConstraint(crv,s,mo=1,w=1)
        else:
            crv=self.curveShapes(shapeNum)
            ggrp=cmds.group(crv,n=crv+'_grp')
            dgrp=cmds.group(ggrp,n=crv+'_sdk')
            zgrp=cmds.group(dgrp,n=crv+'_zero')
    
    def replaceCurveControl(self,shapeNum):
        sel=cmds.ls(sl=1)
        curves=[]
        for s in sel:
            shapes=cmds.listRelatives(s,s=1)
            if shapes:
                for sp in shapes:
                    if cmds.objectType(sp)=='nurbsCurve':
                        curves.append(s)
        for o in curves:
            ctemp=self.curveShapes(shapeNum,objName='simplpleCreaiteTemp')
            cshp=cmds.listRelatives(ctemp,s=1)
            oshp=cmds.listRelatives(o,s=1)
            cmds.parent(cshp,o,s=1,add=1)
            cmds.delete(ctemp,oshp)
            ost=cmds.listRelatives(o,s=1)
            cmds.rename(ost,o+'Shape')  


